import React, { Component } from 'react';
import api from "../../api.js";
import privado from "../../assets/images/privado.svg";
import olho_icone from "../../assets/images/olho.svg";
import dinheiro from "../../assets/images/dinheiro.svg";

class Conta extends Component {
  constructor() {
      super();
      this.state = {salario:'', dependentes: '', };
      this.Calculo = this.Calculo.bind(this);
      this.setSalario = this.setSalario.bind(this);
      this.setDependentes = this.setDependentes.bind(this);

  }


  Calculo(event) {
      event.preventDefault();

      $.ajax({
          url: "http://127.0.0.1:5000/calculo",
          contentType: "application/json",
          dataType: "json",
          type: "post",
          data: JSON.stringify({
              salario: this.state.salario,
              dependentes: this.state.dependentes,
          }),
          success: function(response) {
              this.props.callbackAtualiza(response);
          }.bind(this)


      });
  }



  setSalario(event) {
      this.setState({salario:event.target.value});
  }

  setDependentes(event) {
      this.setState({dependentes:event.target.value});
  }


  render() {

    return (


    <div className="box">
      <h5>Este cálculo demostra como obter um valor de salário líquido a partir do salário bruto e dos principais descontos do salário (INSS e IRRF)</h5>
      <div style={{ fontSize: "26px", padding: "20px 0" }}>

        <span>
          <img className="imagem-icone" src={dinheiro} alt="Ícone Saldo" />
        </span>
        {toggleState ? (

            <div style={{ fontSize: "15px", padding: "15px 0" }}>
            <span className="detalhe">Digite seu salário: </span> 
            <input type="number" step="0.01" min="0" ></input>

          <div style={{ fontSize: "15px", padding: "15px 0" }}>
          <span className="detalhe">Número De Dependentes: </span> 
          <input type="number" step="0.01" min="0" ></input>
        </div>
        <button className="btn" href={api}>Calcular</button>
        </div>
        ) : null}
      </div>

      <button className="btn" onClick={toggleHandler}>
        <img
          style={{ marginTop: "1px" }}
          className="imagem-icone"
          src={toggleState ? privado : olho_icone}
          alt="Privacidade do Saldo"
        />
      </button>
    </div>

  );
}
};

export default Conta;
